mapper.update:
UPDATE sup
   SET name = #{name}, genre = #{sgnere}, capa = #{capa}, tel = #{tel},
       bas_addr = #{baseaddr}, det_addr = #{detailaddr}, etc = #{message}, x = #{x}, y = #{y}
 WHERE sno = #{no}

UPDATE stag_phot
   SET phot = #{photo}
 WHERE sno = #{sno} and spno = #{spno}

mapper.select:
select spno
  from stag_phot
 where sno = #{no}

select sno, name, bas_addr, postno, det_addr, genre, capa, tel, per, etc
  from sup
 where sno = #{no}

select spno, phot as photo
  from stag_phot
 where sno = #{no};
